// ============================================
// MATCHING ALGORITHM (Intel Side)
// ============================================

function calculateMatchScore(contract, profile) {
  if (!profile) return { score: 50 };
  let score = 0;
  const capMatches = contract.requiredCapabilities?.filter(c => profile.capabilities?.includes(c)) || [];
  score += (capMatches.length / (contract.requiredCapabilities?.length || 1)) * 40;
  if (!contract.requiredCerts?.length || contract.requiredCerts.some(c => profile.certifications?.includes(c))) score += 25;
  const clearanceLevels = { none: 0, secret: 1, top_secret: 2 };
  if ((clearanceLevels[profile.clearance] || 0) >= (clearanceLevels[contract.requiredClearance] || 0)) score += 20;
  score += 15;
  return { score: Math.round(score) };
}

function calculateWinProbability(matchScore, contract) {
  let prob = matchScore * 0.55 - (contract.competitors?.length || 0) * 4;
  return Math.min(80, Math.max(8, Math.round(prob)));
}

