// TacticalTrace component - currently unused
// eslint-disable-next-line no-unused-vars
function _TacticalTrace() {
  const [traces, setTraces] = useState([]);
  
  useEffect(() => {
    // Generate initial traces
    const generateTrace = (id) => {
      const startEdge = Math.floor(Math.random() * 4); // 0=top, 1=right, 2=bottom, 3=left
      const startPos = Math.random() * 80 + 10; // 10-90% along edge
      
      let x, y;
      if (startEdge === 0) { x = startPos; y = 0; }
      else if (startEdge === 1) { x = 100; y = startPos; }
      else if (startEdge === 2) { x = startPos; y = 100; }
      else { x = 0; y = startPos; }
      
      // Generate angular path with 2-4 segments
      const segments = 2 + Math.floor(Math.random() * 3);
      let path = `M${x} ${y}`;
      
      for (let i = 0; i < segments; i++) {
        const horizontal = Math.random() > 0.5;
        const distance = 15 + Math.random() * 30;
        const direction = Math.random() > 0.5 ? 1 : -1;
        
        if (horizontal) {
          x = Math.max(5, Math.min(95, x + distance * direction));
        } else {
          y = Math.max(5, Math.min(95, y + distance * direction));
        }
        path += ` L${x} ${y}`;
      }
      
      return {
        id,
        path,
        delay: Math.random() * 8,
        duration: 4 + Math.random() * 4,
        opacity: 0.15 + Math.random() * 0.15,
      };
    };
    
    // Create initial set of traces
    const initialTraces = Array.from({ length: 6 }, (_, i) => generateTrace(i));
    setTraces(initialTraces);
    
    // Regenerate traces periodically to create evolution
    const interval = setInterval(() => {
      setTraces(prev => {
        const newTraces = [...prev];
        const indexToReplace = Math.floor(Math.random() * newTraces.length);
        newTraces[indexToReplace] = generateTrace(Date.now());
        return newTraces;
      });
    }, 3000);
    
    return () => clearInterval(interval);
  }, []);
  
  return (
    <svg 
      style={{ 
        position: "absolute", 
        inset: 0, 
        width: "100%", 
        height: "100%", 
        pointerEvents: "none",
        zIndex: 1,
      }} 
      viewBox="0 0 100 100" 
      preserveAspectRatio="none"
    >
      <defs>
        <filter id="tacticalGlow" x="-50%" y="-50%" width="200%" height="200%">
          <feGaussianBlur stdDeviation="0.5" result="blur" />
          <feMerge>
            <feMergeNode in="blur" />
            <feMergeNode in="SourceGraphic" />
          </feMerge>
        </filter>
      </defs>
      
      {traces.map((trace) => (
        <g key={trace.id}>
          <path
            d={trace.path}
            fill="none"
            stroke={colors.cyan}
            strokeWidth="0.3"
            strokeDasharray="2 1.5"
            filter="url(#tacticalGlow)"
            style={{
              opacity: trace.opacity,
              strokeDashoffset: 100,
              animation: `dataFlow ${trace.duration}s linear ${trace.delay}s infinite`,
            }}
          />
          {/* Small node at end of path */}
          <circle
            cx={trace.path.split(' ').slice(-2)[0].replace('L', '')}
            cy={trace.path.split(' ').slice(-1)[0]}
            r="0.8"
            fill={colors.cyan}
            style={{
              opacity: 0,
              animation: `nodePulse ${trace.duration}s ease-out ${trace.delay + trace.duration * 0.6}s infinite`,
            }}
          />
        </g>
      ))}
    </svg>
  );
}

