function DynamicCrosshair() {
  const [position, setPosition] = useState({ x: -100, y: -100 });
  const [isClicking, setIsClicking] = useState(false);
  const [isHovering, setIsHovering] = useState(false);
  const [clickRings, setClickRings] = useState([]);
  const [rotation, setRotation] = useState(0);
  
  useEffect(() => {
    const handleMouseMove = (e) => {
      setPosition({ x: e.clientX, y: e.clientY });
      const target = e.target;
      const isInteractive = target.tagName === 'BUTTON' || target.tagName === 'A' || target.closest('button') || target.closest('a') || target.getAttribute('role') === 'button';
      setIsHovering(isInteractive);
    };
    
    const handleMouseDown = (e) => {
      setIsClicking(true);
      setClickRings(prev => [...prev, { id: Date.now(), x: e.clientX, y: e.clientY }]);
      setTimeout(() => setClickRings(prev => prev.slice(1)), 500);
    };
    
    const handleMouseUp = () => setIsClicking(false);
    
    window.addEventListener('mousemove', handleMouseMove);
    window.addEventListener('mousedown', handleMouseDown);
    window.addEventListener('mouseup', handleMouseUp);
    
    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mousedown', handleMouseDown);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, []);
  
  useEffect(() => {
    const interval = setInterval(() => {
      setRotation(r => r + 0.5);
    }, 30);
    return () => clearInterval(interval);
  }, []);
  
  const color = isHovering ? colors.gold : colors.cyan;
  const glowColor = isHovering ? colors.goldGlow : colors.cyanGlow;
  const size = isClicking ? 28 : 36;
  
  return (
    <div style={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', pointerEvents: 'none', zIndex: 9999 }}>
      {/* Click rings */}
      {clickRings.map(ring => (
        <div key={ring.id} style={{
          position: 'absolute',
          left: ring.x,
          top: ring.y,
          width: 40,
          height: 40,
          border: `2px solid ${color}`,
          borderRadius: '50%',
          transform: 'translate(-50%, -50%)',
          animation: 'crosshairRing 0.5s ease-out forwards',
        }} />
      ))}
      
      {/* Outer rotating ring */}
      <svg
        style={{
          position: 'absolute',
          left: position.x,
          top: position.y,
          width: size + 16,
          height: size + 16,
          transform: `translate(-50%, -50%) rotate(${rotation}deg)`,
          opacity: 0.4,
          transition: 'width 0.15s, height 0.15s',
        }}
        viewBox="0 0 52 52"
      >
        <circle cx="26" cy="26" r="24" fill="none" stroke={color} strokeWidth="1" strokeDasharray="8 12" />
      </svg>
      
      {/* Inner counter-rotating ring */}
      <svg
        style={{
          position: 'absolute',
          left: position.x,
          top: position.y,
          width: size + 8,
          height: size + 8,
          transform: `translate(-50%, -50%) rotate(${-rotation * 2}deg)`,
          opacity: 0.3,
          transition: 'width 0.15s, height 0.15s',
        }}
        viewBox="0 0 44 44"
      >
        <circle cx="22" cy="22" r="20" fill="none" stroke={color} strokeWidth="1" strokeDasharray="4 8" />
      </svg>
      
      {/* Main crosshair */}
      <svg
        style={{
          position: 'absolute',
          left: position.x,
          top: position.y,
          width: size,
          height: size,
          transform: 'translate(-50%, -50%)',
          filter: `drop-shadow(0 0 8px ${glowColor})`,
          transition: 'width 0.1s ease-out, height 0.1s ease-out',
        }}
        viewBox="0 0 36 36"
      >
        {/* Top line */}
        <line x1="18" y1="2" x2="18" y2="12" stroke={color} strokeWidth="2" strokeLinecap="round" style={{ animation: 'crosshairPulse 2s infinite' }} />
        {/* Bottom line */}
        <line x1="18" y1="24" x2="18" y2="34" stroke={color} strokeWidth="2" strokeLinecap="round" style={{ animation: 'crosshairPulse 2s infinite 0.5s' }} />
        {/* Left line */}
        <line x1="2" y1="18" x2="12" y2="18" stroke={color} strokeWidth="2" strokeLinecap="round" style={{ animation: 'crosshairPulse 2s infinite 1s' }} />
        {/* Right line */}
        <line x1="24" y1="18" x2="34" y2="18" stroke={color} strokeWidth="2" strokeLinecap="round" style={{ animation: 'crosshairPulse 2s infinite 1.5s' }} />
        {/* Center dot */}
        <circle cx="18" cy="18" r={isClicking ? 4 : 2} fill={color} style={{ transition: 'r 0.1s' }}>
          <animate attributeName="opacity" values="1;0.5;1" dur="1s" repeatCount="indefinite" />
        </circle>
        {/* Corner brackets */}
        <path d="M6,10 L6,6 L10,6" fill="none" stroke={color} strokeWidth="1.5" opacity="0.6" />
        <path d="M26,6 L30,6 L30,10" fill="none" stroke={color} strokeWidth="1.5" opacity="0.6" />
        <path d="M30,26 L30,30 L26,30" fill="none" stroke={color} strokeWidth="1.5" opacity="0.6" />
        <path d="M10,30 L6,30 L6,26" fill="none" stroke={color} strokeWidth="1.5" opacity="0.6" />
      </svg>
    </div>
  );
}

